package com.example.warrantytracker;

import android.app.Application;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;

public class MyApplication extends Application {
    private static List<dvice> deviceList = new ArrayList<dvice>();
    private static int nextId = 10;

    public MyApplication() {
        fillDeviceList();
    }


    private void fillDeviceList() {
        dvice d0 = new dvice(0, "MAC BOOK", 2020, "data:image/jpeg;base64,");
        dvice d1 = new dvice(1, "Samsung TV", 2021, "image/jpeg;base64,");
        dvice d2 = new dvice(2, "IPhone", 2020,"data:image/jpeg;base64,");
        dvice d3 = new dvice(3, "Galaxy s21 Ultra", 2020, "data:image/jpeg;base64,");
        dvice d4 = new dvice(4, "Play Station 5", 2021,"data:image/jpeg;base64,");
        dvice d5 = new dvice(5, "Xbox Series X", 2021, "data:image/jpeg;base64,");
        dvice d6 = new dvice(6, "NetGear NightHawk", 2019, "data:image/jpeg;base64,");
        dvice d7 = new dvice(7, "Refrgerator", 2016, "data:image/jpeg;base64,");
        dvice d8 = new dvice(8, "Washer Machine", 2018,"data:image/jpeg;base64,");
        dvice d9 = new dvice(9, "Microwave", 2022,"data:image/jpeg;base64,");

        deviceList.addAll(Arrays.asList( new dvice[] {d0, d1, d2, d3, d4, d5, d6, d7, d8,d9}));

    }

    public static List<dvice> getDeviceList() {
        return deviceList;
    }

    public static void setDeviceList(List<dvice> deviceList) {
        MyApplication.deviceList = deviceList;
    }

    public static int getNextId() {
        return nextId;
    }

    public static void setNextId(int nextId) {
        MyApplication.nextId = nextId;
    }
}
